import { Component } from "react";
import Link from 'next/link'
import Slider from "react-slick";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faFacebook, faInstagram, faVk } from "@fortawesome/free-brands-svg-icons"

import styles from './styles.module.scss'


class SlideCard extends Component {
  render() {
    const { post, ...props } = this.props;
    const bgImage = { backgroundImage: `url(/images/${post.image})` };

    return (
      <div className={styles.card} {...props}>
        
        <div className={styles.wrapper}>
          <div className={styles.header}>

            <div className={styles.date}>25 июня 2020</div>
          </div>

          <div className={styles.data}>
            <div class="content">
              <h1 class="title"><a href="#">Stranger Things: The sound of the Upside Down</a></h1>
              <p class="text">The antsy bingers of Netflix will eagerly anticipate the digital release of the Survive soundtrack, out today.</p>
            </div>
          </div>
          <img src={`/images/${post.image}`} alt="alt text" />
        </div>
      </div>
    );
  }
}


class AutoPlaySlider extends Component {
  render() {
    const { posts, ...props } = this.props

    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      // autoplay: true,
      speed: 10000,
      // autoplaySpeed: 100,
      // cssEase: "ease-in-out",
      cssEase: "linear",
      // swipeToSlide: true,
      // pauseOnHover: true,
      // nextArrow: <SampleNextArrow />,
      // prevArrow: <SamplePrevArrow />,
    };
    return (
      <div className={styles.container}>
        <Slider {...settings}>
          {posts.map((post, i) => (
            <SlideCard key={i} post={post} />
          ))}
        </Slider>
      </div>
    );
  }
}


function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "red" }}
      onClick={onClick}
    />
  );
}


function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "green" }}
      onClick={onClick}
    />
  );
}

export default AutoPlaySlider
