import withApollo from '@/lib/withApollo'
import { gql } from 'apollo-boost'
import { useQuery } from '@apollo/react-hooks'

import PostsMasonry from '@/components/feed/PostsMasonry'


const QUERY = gql`
  query postsByCategory($categorySlug: String){
    getPostsByCategory(categorySlug: $categorySlug) {
      id
      image
      title
      created_at
      category
      description
      author
      tags
      slug
    }
  }
`


const Category = ({ categorySlug }) => {
  console.log('Cat slug: ' + categorySlug);
  const { loading, error, data } = useQuery(QUERY, {
    variables: {
      categorySlug
    }
  });

  if (error)
    return <h1>Error</h1>
  if (loading)
    return <h1>Loading...</h1>;

  const fetchedPosts = data.getPostsByCategory.map(post => (
    {
      title: post.title,
      datetime: post.created_at,
      category: post.category,
      tags: post.tags,
      image: post.image,
      slug: post.slug
    }
  ))

  return (
    <main className="home">
      <section className="container">
        <div className="row">
          <section className="featured-posts-container">
            <PostsMasonry posts={fetchedPosts} columns={2} tagsOnTop={true} />
          </section>
        </div>
      </section>
    </main>
  )
}

export default withApollo({"ssr": true})(Category)
