import classnames from 'classnames'
import { gql } from 'apollo-boost'
import { useQuery } from '@apollo/react-hooks'

import styles from './styles.module.scss'
import AutoPlaySlider from '@/components/sliders/IndexSlider'

const GET_RECENT_POSTS = gql`
  query getPostByType($type: String) {
    getPostBySlug(type: $type) {
      title
      description
      content
      image
      created_at
      # cat_name
      # cat_slug
      # tags_names
    }
  }
`


const IndexLayout = () => {  
  const { loading, error, data } = useQuery(GET_RECENT_POSTS, {
    variables: {
      type: 'recent'
    }
  })
  if (error) return <h1>Error</h1>
  if (loading) return <h1>Loading...</h1>

  const fetchedPost = data.getPostByType

  return <>
    <AutoPlaySlider posts/>
  </>
}

export default IndexLayout
