import { Component } from "react";
import Slider from "react-slick";

import styles from './styles.module.scss'


class SlideCard extends Component {
  render() {
    const { data, key, ...props } = this.props;
    return (
      <div key={key} {...props}>
        <div className={styles.card}>
          Опа!
        </div>
      </div>
    );
  }
}


class AutoPlaySlider extends Component { 
  render() {
    const { posts, ...props } = this.props

    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      // autoplay: true,
      speed: 10000,
      // autoplaySpeed: 100,
      // cssEase: "ease-in-out",
      cssEase: "linear",
      // swipeToSlide: true,
      // pauseOnHover: true,
      // nextArrow: <SampleNextArrow />,
      // prevArrow: <SamplePrevArrow />,
    };
    return (
      <div className={styles.container}>
        <Slider {...settings}>
          {posts.map((post, i) => (
            <SlideCard className={styles.card} key={i} post />
          ))}
          {/* {[...Array(5)].map((_, i) => 
            <SlideCard key={i} className={styles.slide}/>
          )} */}
          
        </Slider>
      </div>
    );
  }
}


function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "red" }}
      onClick={onClick}
    />
  );
}


function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "green" }}
      onClick={onClick}
    />
  );
}

export default AutoPlaySlider
