import React, { Component } from "react";
import Link from 'next/link'
import Slider from "react-slick";

import styles from './styles.module.scss'
import classnames from 'classnames'


class SlideCard extends Component {
  render() {
    const { post, ...props } = this.props;
    const bgImage = { backgroundImage: `url(/images/${post.image})` };

    return (
      <div {...props}>
        <Link href="/post/[slug]" as={`/post/${post.slug}`}>
          <a className={styles.card} style={bgImage}>

            <div className={styles.header}>
              <div className={styles.date}>25 июня 2020</div>
            </div>

            <div className={styles.data}>
              <div className={styles.content}>
                <h2 className={styles.title}>Stranger Things: The sound of the Upside Down</h2>
                <p className={styles.text}>The antsy bingers of Netflix will eagerly anticipate the digital release of the Survive soundtrack, out today.</p>
              </div>
            </div>

          </a>
        </Link>
      </div>
    );
  }
}


class AutoPlaySlider extends Component {
  render() {
    const { posts, ...props } = this.props

    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      speed: 1000,
      // autoplaySpeed: 100,
      cssEase: "ease-in-out",
      // cssEase: "linear",
      // swipeToSlide: true,
      // pauseOnHover: true,
      arrows: false,
      nextArrow: <SampleNextArrow />,
      prevArrow: <SamplePrevArrow />,
    };
    return (
      <div className={styles.sliderWrapper}>
        <Slider {...settings}>
          {posts.map((post, i) => (
            <SlideCard key={i} post={post} />
          ))}
        </Slider>
      </div>
    );
  }
}


function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={classnames(className, styles.nextArrow)}
      onClick={onClick}
    />
  );
}


function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={classnames(className, styles.prevArrow)}
      onClick={onClick}
    />
  );
}

export default AutoPlaySlider
