import { useState, useMemo } from 'react'
import { useQuery } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'

import useUpdateEffect from './hooks'
import styles from './styles.module.scss'
import PostsGrid from '@/components/feed/PostsGrid'


const GET_POSTS = gql`
  query getPostsByCategory($catSlug: String, $pass: Int, $quantity: Int) {
    getPostsByCategory(catSlug: $catSlug, pass: $pass, quantity: $quantity) {
      id
      image
      title
      content
      created_at
      category
      description
      author
      tags
      slug
    }
  }
`


const CategoryLayout = ({ slug: catSlug, pass, quantity }) => {
  const [pageSize, setPageSize] = useState(9)
  const [current, setCurrent] = useState(1);

  useUpdateEffect(() => {
    window.scroll({ top: 0, behavior: 'smooth' })
  }, [current, pageSize])

  const { loading, error, data } = useQuery(GET_POSTS, {
    variables: {
      catSlug,
      pass,
      quantity
    }
  });
  if (error) return <h1>Error</h1>
  if (loading) return <h1>Loading...</h1>

  const posts = data.getPostsByCategory.map(post => ({
    title: post.title,
    description: post.description,
    content: post.content,
    datetime: post.created_at,
    category: post.category,
    tags: post.tags,
    image: post.image,
    slug: post.slug
  }
  ))

  const paginatedPosts = useMemo(() => {
    const lastIndex = current * pageSize
    const firstIndex = lastIndex - pageSize
    return posts.slice(firstIndex, lastIndex)
  }, [current, pageSize, posts])

  return (
    <section className={styles.container}>
      <div className={styles.row}>
        <section className={styles.featuredPostsContainer}>
          <PostsGrid posts={paginatedPosts} />
          <Pagination
            // showSizeChanger
            onShowSizeChange={setPageSize}
            pageSize={pageSize}
            total={posts.length}
            defaultCurrent={current}
            onChange={setCurrent}
          />
        </section>
      </div>
    </section>
  )
}

export default CategoryLayout
