import { useState, useMemo } from 'react'
import { useQuery } from '@apollo/react-hooks'
import { gql } from 'apollo-boost'
import { Pagination } from 'antd'

import useUpdateEffect from './hooks'
import styles from './styles.module.scss'
import PostsGrid from '@/components/feed/PostsGrid'


const GET_POSTS = gql`
  query getPostsByCategory($catSlug: String, $offset: Int, $limit: Int) {
    getPostCount
    getPostsByCategory(catSlug: $catSlug, offset: $offset, limit: $limit) {
      id
      image
      title
      content
      created_at
      category
      description
      author
      tags
      slug
    }
  }
`


const CategoryLayout = ({ slug: catSlug }) => {
  const defaultLimit = 9

  const { loading, error, data } = useQuery(GET_POSTS, {
    variables: {
      catSlug,
      offset: 0,
      limit: defaultLimit
    }
  })
  if (error) return <h1>Ошибка!</h1>
  if (loading) return <h1>Загрузка...</h1>
  
  const posts = data.getPostsByCategory.map((post) => ({
    title: post.title,
    description: post.description,
    content: post.content,
    datetime: post.created_at,
    category: post.category,
    tags: post.tags,
    image: post.image,
    slug: post.slug
  }))

  const [pageSize, setPageSize] = useState(defaultLimit)
  // const [current, setCurrent] = useState(1)
  // const [lastIndex, setLastIndex] = useState(current * pageSize)
  // const [firstIndex, setFirstIndex] = useState(lastIndex - pageSize)
  // const [postCount, setPostCount] = useState(data.getPostCount);

  // useUpdateEffect(() => {
  //   window.scroll({ top: 0, behavior: 'smooth' })
  // }, [current, pageSize])

  // const onShowSizeChange = (_, pageSize) => {
  //   setCurrent(1)
  //   setPageSize(pageSize)
  //   setFirstIndex(1)
  //   setLastIndex(pageSize)
  //   onFetchMore(firstIndex - 1, pageSize)
  // }

  // const onCurrentChange = (page) => {
  //   setCurrent(page)
  //   setLastIndex(current * pageSize)
  //   setFirstIndex(lastIndex - pageSize)
  //   onFetchMore(firstIndex - 1, pageSize)
  // }

  // const onFetchMore = (offset, limit) => {
  //   fetchMore({
  //     variables: {
  //       catSlug,
  //       offset,
  //       limit
  //     },
  //     updateQuery: (_, { fetchMoreResult }) => {
  //       if (!fetchMoreResult)
  //         <h1>Couldn't load other posts...</h1>
  //       setPostCount(fetchMoreResult.getPostCount)
  //       return fetchMoreResult
  //     }
  //   })
  // }

  return (
    <section className={styles.container}>
      <div className={styles.row}>
        <section className={styles.featuredPostsContainer}>
          <PostsGrid posts={posts} />
          <Pagination
            showSizeChanger
            // onShowSizeChange={onShowSizeChange}
            // pageSize={pageSize}
            // total={postCount}
            // defaultCurrent={current}
            // onChange={onCurrentChange}
          />
        </section>
      </div>
    </section>
  )
}

export default CategoryLayout
