import { useState } from 'react'
import Link from 'next/link';

import Logo from '@/components/Logo';
import getFilenameFromPath from '@/services/getFilenameFromPath'


const navigationMenuItems = [
  {
    title: 'Звёзды',
    path: 'stars'
  },
  {
    title: 'Кино',
    path: 'cinema'
  },
  {
    title: 'Мода',
    path: 'fashion'
  },
  {
    title: 'Блогеры',
    path: 'home'
  }
]

const Navigation = () => {
  const [menuActive, setMenuActive] = useState(false);
  
  const mobileNavMenuIcon = '/images/mobile-navigation-menu-icon.png'
  const altMobileNavMenuIcon = getFilenameFromPath()

  return (
    <nav className="navigation">
      <div className="top-stripe">
        <span className="top-stripe-description">
          НОВОСТИ ШОУ-БИЗНЕСА
        </span>
      </div>

      <Logo />

      <div className={`menu-container${menuActive ? ' active' : ''}`}>
        <ul className="menu-list">
          {navigationMenuItems.map((link, index) => (
            <li className="menu-item" key={index}>
              <Link href={link.path}>
                <a className="menu-item-link">
                  {link.title}
                </a>
              </Link>
            </li>
          ))}
        </ul>
      </div>
      <img src={mobileNavMenuIcon} alt={mobileNavMenuIcon} className="navicon" onClick={() => { setMenuActive(!menuActive) }} />
    </nav>
  )
}

export default Navigation
