import { useRouter } from 'next/router'
import { gql } from 'apollo-boost'
import { useQuery } from '@apollo/react-hooks'

import styles from '@/styles/pages/category.module.scss'
import MainLayout from '@/components/MainLayout'
import PostsMasonry from '@/components/'


const GET_POSTS = gql`
  query getPostsByCategory($catSlug: String){
    getPostsByCategory(catSlug: $catSlug) {
      id
      image
      title
      content
      created_at
      category
      description
      author
      tags
      slug
    }
  }
`


const CategoryPage = () => {
  const router = useRouter()

  const meta = {
    title: 'a',
    keywords: [
      'a'
    ],
    description: 'a'
  }

  const { query: { slug } } = router

  const { loading, error, data } = useQuery(GET_POSTS, {
    variables: {
      catSlug: slug
    }
  });
  if (error) return <h1>Error</h1>
  if (loading) return <h1>Loading...</h1>

  const fetchedPosts = data.getPostsByCategory.map(post => ({
    title: post.title,
    description: post.description,
    content: post.content,
    datetime: post.created_at,
    category: post.category,
    tags: post.tags,
    image: post.image,
    slug: post.slug
  }))

  return (
    <MainLayout meta={meta}>
      <section className={styles.container}>
        <div className={styles.row}>
          <section className={styles.featuredPostsContainer}>
            <PostsMasonry posts={fetchedPosts} columns={2} tagsOnTop={true} />
          </section>
        </div>
      </section>
    </MainLayout >
  )
}

export default CategoryPage
