const getFilenameFromPath = (path, withExtension = false) => {
  const regex = withExtension ? /(^.*[\\\/])(.+?)/ : /(^.*[\\\/])(.+?)(\.[^.]*$|$)/
  const name = path.replace(regex, '')
  return name
}

console.log(getFilenameFromPath('/images/mobile-navigation-menu-icon.png', false));

export default getFilenameFromPath
