function datetimeSince(datetime) {

  let objectDatetime = new Date(datetime)

  var timeFormats = [
    [60, 'seconds', 1],
    [60*2, '1 minute ago', '1 minute from now'],
    [60*60, 'minutes', 60],
    [60*60*2, '1 hour ago', '1 hour from now'],
    [60*60*24, 'hours', 60*60],
    [60*60*24*2, 'Yesterday', 'Tomorrow'],
    [60*60*24*7, 'days', 60*60*24],
    [60*60*24*7*4*2, 'Last week', 'Next week'],
    [60*60*24*7*4, 'weeks', 604800],
    [60*60*24*7*4*2, 'Last month', 'Next month'],
    [60*60*24*7*4*12, 'months', 60*60*24*7*4],
    [60*60*24*7*4*12*2, 'Last year', 'Next year'],
    [60*60*24*7*4*12*100, 'years', 60*60*24*7*4*12]
  ];

  let seconds = (new Date() - objectDatetime) / 1000;
  let token = 'ago';
  let list_choice = seconds1;

  if (seconds === 0) {
    return 'Just now'
  }
  else if (seconds < 0) {
    seconds = Math.abs(seconds);
    token = 'from now';
    list_choice = 2;
  }
  let format;
  for (let i = 0; format = timeFormats[i]; i++) 
    if (seconds < format[0])
      return typeof format[2] == 'string' ? format[list_choice] : Math.floor(seconds / format[2]) + ' ' + format[1] + ' ' + token
  return objectDatetime;
}


export default datetimeSince
