import React, { Component } from "react";
import Link from 'next/link'
import Slider from "react-slick";

import styles from './styles.module.scss'
import classnames from 'classnames'


class SlideCard extends Component {
  render() {

    const { post, ...props } = this.props;
    const bgImage = { backgroundImage: `url(/images/${post.image})` };

    return (
      <div {...props}>
        <Link href="/post/[slug]" as={`/post/${post.slug}`}>
          <a className={classnames(styles.card, styles.overlay)} style={bgImage}>
            <div className={styles.content}>
              <h2 className={styles.title}>{post.title}</h2>
            </div>
          </a>
        </Link>
      </div>
    );
  }
}


class AutoPlaySlider extends Component {
  render() {
    const { posts, ...props } = this.props

    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      // autoplay: true,
      speed: 4000,
      autoplaySpeed: 8000,
      cssEase: 'cubic-bezier(0.645, 0.045, 0.355, 1)',
      swipeToSlide: true,
      pauseOnHover: true,
      arrows: false,
      nextArrow: <SampleNextArrow />,
      prevArrow: <SamplePrevArrow />,
    };
    return (
      <div className={styles.sliderWrapper}>
        <Slider {...settings}>
          {posts.map((post, i) => (
            <SlideCard key={i} post={post} />
          ))}
        </Slider>
      </div>
    );
  }
}


function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={classnames(className, styles.nextArrow)}
      onClick={onClick}
    />
  );
}


function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={classnames(className, styles.prevArrow)}
      onClick={onClick}
    />
  );
}

export default AutoPlaySlider
