import React, {useState} from 'react'
import {withApollo} from 'next-apollo'
import Head from 'next/head'
import Link from 'next/link'

import CategoryPage from '../components/body/CategoryPage'


const ShowPosts = (category_id) => <CategoryPage category_id={category_id} />

const Index = props => {
  const {categoryId, setCategoryId} = useState(0)

  return (
    <>
      <div>
        <input type='text' onChange={e => ShowPosts(e.target.value)}/>
      </div>
      <div>
        <CategoryPage category_id={categoryId} />
      </div>
    </>
  )
}

export default withApollo({ ssr: true })(Index)
