import { useState } from 'react'
import Link from 'next/link';

import styles from './style.module.scss'
import Logo from '@/components/Logo';
import getFilenameFromPath from '@/services/getFilenameFromPath'



const navigationMenuItems = [
  {
    title: 'Звёзды',
    path: 'stars'
  },
  {
    title: 'Кино',
    path: 'cinema'
  },
  {
    title: 'Мода',
    path: 'fashion'
  },
  {
    title: 'Блогеры',
    path: 'bloggers'
  }
]

const Navigation = () => {
  const [menuActive, setMenuActive] = useState(false);
  
  const mobileNavMenuIcon = '/icons/mobile-navigation-menu-icon.png'
  const altMobileNavMenuIcon = getFilenameFromPath(mobileNavMenuIcon, false)

  return (
    <nav className={styles.navigation}>
      <div className={styles.topStripe}>
        <span className={styles.topStripeDescription}>
          НОВОСТИ ШОУ-БИЗНЕСА
        </span>
      </div>

      <Logo />

      <div className={`menu-container${menuActive ? ' active' : ''}`}>
        <ul className="menu-list">
          {navigationMenuItems.map((link, index) => (
            <li className="menu-item" key={index}>
              <Link href={link.path}>
                <a className="menu-item-link">
                  {link.title}
                </a>
              </Link>
            </li>
          ))}
        </ul>
      </div>
      <img src={mobileNavMenuIcon} alt={altMobileNavMenuIcon} className={navIcon} onClick={() => { setMenuActive(!menuActive) }} />
    </nav>
  )
}

export default Navigation
