import classnames from 'classnames'
import { useState, useMemo } from 'react'
import Link from 'next/link'
import { Pagination } from 'antd'

import styles from './styles.module.scss'
import useUpdateEffect from './hooks'
import TagRow from '@/components/feed/TagRow'


const PostsGrid = ({ posts }) => {
  const [pageSize, setPageSize] = useState(9)
  const [current, setCurrent] = useState(1);

  const paginatedPosts = useMemo(() => {
    const lastIndex = current * pageSize
    const firstIndex = lastIndex - pageSize

    return posts.slice(firstIndex, lastIndex)
  }, [current, pageSize, posts])

  useUpdateEffect(() => {
    window.scroll({
      top: 0,
      behavior: 'smooth'
    })
  }, [current, pageSize])

  return (
    <section className={styles.gridPaginationContainer}>
      <section className={classnames(styles.postGrid, styles.container)}>
        {paginatedPosts.map((post, index) => (
          <div className={styles.postContainer} key={index}>
            <figure>
              {/* <Link href={`/post/${post.slug}`} replace={true}> */}
              <Link href={`/post/${post.slug}`} replace={true}>
                <a>
                  <img src={`/images/${post.image}`} alt={post.image} />
                </a>
              </Link>
            </figure>

            <TagRow tags={post.tags} />

            <h2>
              {post.title}
            </h2>

            <p className={styles.authorText}>
              <span>
                {post.created_at}
              </span>
            </p>

            <p className={styles.descriptionText}>
              {post.description}
            </p>

            <Link href={`post/${post.slug}`}>
              <a>Подробнее</a>
            </Link>
          </div>
        ))}
      </section>

      <Pagination
        // showSizeChanger
        onShowSizeChange={setPageSize}
        pageSize={pageSize}
        total={posts.length}
        defaultCurrent={current}
        onChange={setCurrent}
      />
    </section>
  )
}

export default PostsGrid
