  'Tag3': 'rgb(255, 45, 85)',
  'Tag4': 'rgb(100, 100, 100)'
}

const TagRow = ({ tags }) => {
  return (
    <div className={styles.tagsContainer}>
      {tags.map((tag, index) =>
        <Link href={'/tag/['} as={`/tag/${post.slug}`}>
          <a key={index} className={styles.tag} style={{ backgroundColor: categoryColors[tag] }}>
            {tag.toUpperCase()}
          </a>
        </Link>
      )}
    </div>
  );
}

export default TagRow
